#bd, .yui-g, .yui-gb, .yui-gc, .yui-gd, .yui-ge, .yui-gf {
}
html {
background-color:#000000 !important;
color:#FFCC00 !important;
}
div {
background-color:#000000 !important;
color:#FFCC00 !important;
}
a {
color:#FF9900 !important;
}
.movie-block, .movie-row {
