css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bbc.co.uk") {
    body#body {width:99% !important;margin:0 auto !important;}
    .centerbody,
    .centerbodywide {position:absolute !important;left:20px !important;right:20px !important;width:auto !important;}
    table.main,
    td.storybody,
    #blq-accesslinks,
    #blq-mast,
    #blq-main,
    table.storycontent,
    div.centerbody table,
    div.newsbanner {width:99% !important;}
    td.storybody table {float:right;width:240px !important;}
}
