css:
@-moz-document domain("news.ycombinator.com") {
/* news.ycombinator.com */

body {font-size: 16px}
.pagetop {font-size: 1rem}
.pagetop b {font-size: .9rem}
.title {font-size: 1rem}
.comhead {font-size: .9rem}
.subtext {font-size: .9rem}
.comment {font-size: .9rem}

@media only screen
and (min-width : 300px)
and (max-width : 750px) {
	span.pagetop {font-size: 1rem}
	span.pagetop b {font-size: .9rem}
	.title {font-size: 1rem}
	.subtext {font-size: .9rem}
	.comhead {font-size: .9rem}
	.comment {font-size: .9rem}
}

}
