@-moz-document domain("autohotkey.com") {
 ::-webkit-scrollbar {
 	/* Width */
 	width: 1.2em!important;
 }

 /* Color of handle ___________________________________________________________________________*/

 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track ______________________________________________________________________________*/

 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom ___________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }
}

@-moz-document domain("autohotkey.com") {
/*_____________________________________________________________________________________*/

* {
	text-decoration: none!important;
}

.codebox,
.ipbfs_titlebox,
.methodShort,
.pagination li a,
.pagination li.active span,
blockquote.ipsBlockquote,
dd
{
	border: none!important;
}

tr {
    border: 1px solid!important;
}

#community_app_menu>li>a {
	text-transform: capitalize!important;
}

kbd {
	text-shadow: none!important;
}

table.info td:first-of-type {
	text-align: center!important;
}

table.info td:nth-child(2) {
	text-align: left!important;
}

.horizontalView .post_block .postInfoBottom,
.horizontalView .post_block ul.post_controls {
	visibility: visible!important;
}

listing,
plaintext,
pre,
xmp {
	white-space: pre-wrap!important;
}

.fixedHeader #branding {
	z-index: 999!important;
}

/*___ Fonts __________________________________________________________________________________*/

pre {
	font-size: 27px!important;
}

a.forumtitle,
a.topictitle {
	font-size: 21px!important;
}

.codebox code,
blockquote.ipsBlockquote,
body {
	font-size: 19px!important;
}

.message,
.post_body .post,
.postbody .content {
	font-size: 18px!important;
}

.codebox,
.forum-description,
.panel p,
blockquote,
code.prettyprint,
dt label,
pre.prettyprint {
	font-size: 17px!important;
}

#primary_nav,
.desc,
.desc.blend_links a,
p.posted_info {
	font-size: 15px!important;
	line-height: 27px!important;
}

#secondary_navigation a {
	font-size: 14px!important;
}

pre {
	line-height: 27px!important;
}

#right .highlight {
    line-height: 39px!important;
}

/*___ Remove Items ___________________________________________________________________________*/

.dragbar {
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

.dragbar {
	display: none!important;
}

/*___ Left/Right/Top/Bottom ___________________________________________________________________*/

#user_navigation {
    margin-top: 46px!important;
}

/*___ Margins _______________________________________________________________________________*/

#right,
.Syntax,
.content-2,
.mbr-section,
blockquote.ipsBlockquote,
fieldset dl,
li {
	margin: 12px!important;
}

body,
.viewer.focused {
	margin: auto!important;
}

#user_navigation a {
	margin: 3px 6px!important;
}

/*___ Paddings ______________________________________________________________________________*/

#right,
.column_view .post_body .post,
.codebox,
blockquote,
td,
blockquote.ipsBlockquote {
	padding: 12px!important;
}

#right .highlight,
.Syntax,
p.CommandSyntax {
    padding: 3px 13px!important;
}

/*___ Heights _______________________________________________________________________________*/

.codebox_plus .collapsed {
	Max-height: none!important;
}

pre[class*="language-"],
.codebox,
.code-toolbar {
	height: auto!important;
}

#branding {
	height: 119px!important;
}

#user_navigation {
    height: 59px!important;
}

.navbar .nav-tabs > ul {
    height: 3rem!important;
}

/*___ Widths ________________________________________________________________________________*/

body {
	max-width: none!important;
}

body {
	width: 98%!important;
}

#left {
	width: 24em!important;
}

fieldset.fields2 dt {
	width: 16rem!important;
}
}

@-moz-document domain("autohotkey.com") {
a[href],
td span {
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited {
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/*________________________________________________________________________________________________________*/

#left .list>a.selected,
.pagination li.active span,
td strong,
a[href]:hover span,
a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label {
	color: #ff0!important;
	text-shadow: 1px 1px #000!important;
}

/*________________________________________________________________________________________________________*/

#right .highlight,
.co1,
.content,
.forum-description,
.panel,
.postbody,
.postprofile,
[style*="color:rgb(85,85,85)"],
body,
code[class*="language-"],
li,
p,
pre,
pre[class*="language-"],
td
{
	color: #fff!important;
	text-shadow: 1px 1px #000!important;
}

/*__ Khaki _______________________________________________________________________________________________*/

td:first-child,
h1,
h2 {
	color: #F0E890!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*__ Burlywood ___________________________________________________________________________________________*/

dt,
h3,
h4,
p strong,
table.info th {
	color: #e0b880!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*__ Sandy Brown _________________________________________________________________________________________*/

.postprofile strong,
h5,
h6,
label {
	color: #F0A060!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Blue ________________________________________________________________________________________________*/

.horizontalInfoList li,
.lastpost,
.published,
.responsive-hide,
[itemprop="dateCreated"],
time {
	color: #07beff!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*________________________________________________________________________________________________________*/

#search_options,
.search-box .inputbox,
.search-box .inputbox:hover,
input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea {
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
}

/*________________________________________________________________________________________________________*/

::selection {
	background: #ff9!important;
	color: #000!important;
}

pre ::selection {
	background: #ff9!important;
	color: #fff!important;
}

code em,
pre.origin em {
	color: #a9a9a9!important;
}

.token.operator,
.token.entity,
.token.url,
.token.variable,
pre>.lab
{
	color: #d7d0f0!important;
}

[style="color:#000000"],
code>.fun,
code>.lab,
pre>.fun {
	color: #c2b2ff!important;
}

code>.num,
pre>.num {
	color: #41ffb9!important;
}

code>.dir,
pre>.dir {
	color: #65ff65!important;
}

.com {
	color: #c2b5b5!important;
}

.clo,
.opn,
.pun {
	color: #ff0!important;
}

code>.biv,
pre>.biv {
	color: #ff8d00!important;
}

.dull,
.headnote,
.ver {
	color: #ffd536!important;
}

.Syntax span.func {
	color: #ffe171!important;
}

.lit,
[style="color: #8383ff;"],
[style="color: blue"] {
	color: #0ff!important;
}

.typ {
    color: #00ffba!important;
}

.pln {
	color: #e7ffb4!important;
}

code>.met,
pre>.met {
	color: #17d4ff!important;
}

code>.str,
pre>.str {
	color: #ff9b9b!important;
}

code>.bif,
code>.cmd,
pre>.bif,
pre>.cmd {
	color: #90b9ff!important;
}

code>.cfs,
code>.dec,
pre>.cfs,
pre>.dec {
	color: #e26cff!important;
}

.kwd {
	color: #cfcfff!important;
}

kbd {
	color: #fffd96;
}

#left .toc li.closed>span,
#left .toc li.opened>span {
	color: #db7d7d!important;
}

.kw1 {
	color: #ff00ff!important;
}

.kw3 {
	color: #399dff!important;
}

.sy0,
.br0 {
	color: white!important;
}

.nu0 {
	color: #f4ff60!important;
}

.coMULTI {
	color: #b8b8b8!important;
}

.re0 {
	color: #ff3690!important;
}

.st0,
.st_h {
	color: #00ff00!important;
}

codebox .S4 {
    color: #fcfcfc!important;
}

codebox .S1 {
    color: #a7a7a7!important;
}

pre .str>.esc, code .str>.esc {
    color: #f9d260!important;
}

.token.boolean,
.token.constant,
.token.deleted,
.token.function-name,
.token.number,
.token.property,
.token.symbol,
.token.tag
{
    color: #ff9e9e;
}




}

@-moz-document domain("autohotkey.com") {
/*___________________________________________________________________________________________*/

html,
body {
	background: url(https://image.ibb.co/hVmiOe/wallpaper_with_black_background_45_free_wallpaper.jpg)!important;
	background-attachment: fixed!important;
}

/*___ Background Gray __________________________________________________________________________*/

.navbar .nav-tabs {
	background-color: #060505!important;
}

/*___ Background 0000 __________________________________________________________________________*/

#community_app_menu>li.active>a,
#community_app_menu>li>a.menu_active,
#community_app_menu>li>a:hover,
#footer,
#head li:hover,
#ipbwrapper,
#search_wrap,
#secondary_navigation,
#user_navigation a:hover,
#vnc_filter_popup_close,
.bar,
.border,
.col_f_icon img,
.column_view .post_body,
.column_view .post_wrap,
.forabg ul.topiclist li.header,
.forumHover tr:hover td,
.forumbg ul.topiclist li.header,
.general_box h3,
.general_box,
.horizontalView .author_info,
.ipbfs_titlebox,
.ipsBox_container,
.ipsBox_highlight,
.ipsBox_notice,
.ipsButton,
.ipsComment,
.ipsFloatingAction,
.ipsForm_submit,
.ipsLikeButton.ipsLikeButton_enabled,
.ipsSideBlock h3,
.ipsSideBlock,
.maintitle,
.maintitle.linkBar,
.no_messages,
.pagination .back a,
.pagination .forward a,
.pagination .page a,
.pagination .pages li.active,
.panel>.inner>h3:first-child,
.panel>h3:first-child,
.poll_question ol,
.popupInner h3,
.popupInner,
.post_block h3,
.post_block,
.search-header .button,
.search-header .post-buttons > #expand > a,
.statistics,
.statistics_head,
.token.entity,
.token.operator,
.token.url, .token.variable,
.topic_buttons li a,
a.ipsButton,
blockquote.ipsBlockquote,
body#ipboard_body fieldset.submit,
body#ipboard_body p.submit,
img[src*='f_icon.png'],
img[src*='f_icon_read.png'],
img[src*='f_redirect.png'],
ul.post_controls,
ul.post_controls a,
ul.post_controls a.ipsButton_secondary
{
	background: rgba(0, 0, 0, 0)!important;
}

/*___ Background-color 0000 ______________________________________________________________________*/

#QuickRef tr:nth-child(even),
#left .list,
.bg2,
.navbar .nav-tabs .tab.dropdown-visible,
.navbar .nav-tabs .tab:hover,
.pagination li a,
.pagination li.active span,
.post_block.row1,
.row1,
[style="background-color:rgb(255,255,255)"],
h4,
table.info th
{
	background-color: rgba(0, 0, 0, 0)!important;
}

/*___ Highlight Background ________________________________________________________________________*/

#left .list>a.selected,
#left .list>a:hover {
	border-left-color: #434343!important;
	background: linear-gradient(to right, #383838, #0000)!important;
}

/*______________________________________________________________________________________________*/

pre,
code {
	background: #111!important;
	background: url(https://image.ibb.co/msdy5U/Red_Fabric.jpg) #111!important;
}

/*__ Orange ________________________________________________________________________________________*/

.btn-inverse .btn-default,
.topic_buttons li.disabled a,
.topic_buttons li.disabled span {
	background-image: linear-gradient(to bottom right, #da3a04, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.btn-inverse .btn-default:hover, 
.topic_buttons li.disabled a:hover,
.topic_buttons li.disabled span:hover {
	background-image: linear-gradient(to bottom right, #eb4b15, #000)!important;
	color: #ff0!important;
}

.btn-inverse .btn-default:active,
.topic_buttons li.disabled a:active,
.topic_buttons li.disabled span:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Green _________________________________________________________________________________________*/

.btn-success {
	background-image: linear-gradient(to bottom right, #81BC2E, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.btn-success:hover {
	background-image: linear-gradient(to bottom right, #92cd3f, #000)!important;
	color: #ff0!important;
}

.btn-success:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Facebook _________________________________________________________________________________________*/

[href*="facebook"] {
	background-image: linear-gradient(to bottom right, #3b5998, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

[href*="facebook"]:hover {
	background-image: linear-gradient(to bottom right, #4c6aa9, #000)!important;
	color: #ff0!important;
}

[href*="facebook"]:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Twitter _________________________________________________________________________________________*/

[href*="twitter"] {
	background-image: linear-gradient(to bottom right, #1da1f2, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

[href*="twitter"]:hover {
	background-image: linear-gradient(to bottom right, #2eb2f3, #000)!important;
	color: #ff0!important;
}

[href*="twitter"]:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Plain Black ______________________________________________________________________________________*/

#left,
#right,
.codebox,
.content-2,
.forabg,
.mbr-section,
.methodShort,
.panel,
.topic.hfeed.clear.clearfix.removeDefault.horizontalView,
table
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

#left:hover,
#right:hover,
.codebox:hover,
.content-2:hover,
.forabg:hover,
.mbr-section:hover,
.methodShort:hover,
.panel:hover,
.topic.hfeed.clear.clearfix.removeDefault.horizontalView:hover,
table:hover
{
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*__ Plain ___________________________________________________________________________________________*/

#login_form,
.dropdown .dropdown-contents,
.forabg ul.topiclist li.row,
.forumbg ul.topiclist li.row,
.popupWrapper,
.post {
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

#login_form:hover,
.dropdown .dropdown-contents:hover,
.forabg ul.topiclist li.row:hover,
.forumbg ul.topiclist li.row:hover,
.popupWrapper:hover,
.post:hover {
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*__ Noise ___________________________________________________________________________________________*/

#branding,
#head,
.navbar,
.page-width {
	background-image: linear-gradient(to bottom, rgba(0, 0, 0, .1), rgba(0, 0, 0, 0)), url(https://image.ibb.co/e39YEk/noise2.jpg)!important;
	background-repeat: repeat-x, repeat!important;
	background-size: 100px 15px, 140px 140px!important;
}

#branding:hover,
#head:hover,
.navbar:hover,
.page-width:hover {
	background-image: linear-gradient(to bottom, rgba(255, 255, 255, .1), rgba(0, 0, 0, 0)), url(https://image.ibb.co/e39YEk/noise2.jpg)!important;
}

/*__ Red Gradient ______________________________________________________________________________________*/

#left .toc li:not(.opened):hover,
.message.error,
.warning,
table tr:hover
{
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

/*__ Blue Gradient ______________________________________________________________________________________*/

.note,
blockquote,
blockquote.ipsBlockquote {
	background: linear-gradient(to bottom, #053177, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

/*__ Green Gradient _____________________________________________________________________________________*/

#left .toc .selected, 
#head .selected,
.navbar .nav-tabs .tab.selected {
	background: linear-gradient(to bottom, #15534d 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	text-shadow: 1px 1px #555!important;
}

/*__ Yellow Gradient _____________________________________________________________________________________*/

selector {
	background: linear-gradient(to bottom, #9d9d5d 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	text-shadow: 1px 1px #555!important;
	margin: 2px!important;
}

/*__ Orange Gradient _____________________________________________________________________________________*/

p.CommandSyntax,
.Syntax,
#right .highlight {
	background: linear-gradient(to bottom, #da3a04 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	text-shadow: 1px 1px #555!important;
}
}

@-moz-document domain("autohotkey.com") {
/*___________________________________________________________________________________________*/

.post,
.forabg ul.topiclist li.row,
.forumbg ul.topiclist li.row
{
	filter: brightness(75%)!important;
}

/*___________________________________________________________________________________________*/

.post:hover,
.forabg ul.topiclist li.row:hover,
.forumbg ul.topiclist li.row:hover
{
	filter: brightness(100%)!important;
}

/*___________________________________________________________________________________________*/

img {
	border-radius: 22px!important;
}

/*___________________________________________________________________________________________*/

img {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

li,
p,
tr {
	filter: brightness(68%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}

li:hover,
p:hover,
selector:hover p,
tr:hover {
	filter: brightness(100%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}


selector {
	opacity: .8!important;
}

.navbar .inner.static {
	opacity: 1!important;
}
}

