css:
@-moz-document domain("echelog.com") {
body,
html {
    min-height: 100%!important;
}

html,
body {
    background-color: #263238!important
}

body p,
td font b {
    color: #FFCB6B!important
}

td i, pre, div.log_menu {
    color: #7a7a7a!important
}

span.a {
    color: #714747cf!important
}

span.b {
    color: #61b15d87!important
}

span.c {
    color: #5452af!important
}

span.d {
    color: #b17a43!important
}

a {
    color: #FFCB6B!important;
    text-decoration: none!important
}

a:hover {
    text-decoration: underline!important
}
}
