css:
/* i really want this to be global */
.section.transparent, body.ie8 .section.transparent {
    background-color: #086f61;
    border: none;
    box-shadow: none;
}
ome #admins-proxy-separator {
    padding-top: 20px;
    background-color: #086f61;
}

.pb-content {
    padding: 20px;
    background: #fff;
    border-bottom-left-radius: 2px;
    border-bottom-right-radius: 2px;
    background-color: #086f61;
}

#mainnav-container #search-bar .search-bar-container .search-bar-input>div {
    display: -ms-flexbox;
    display: flex;
    flex-direction: row;
    justify-content: flex-start;
    align-items: stretch;
    background-color: #086f61;
}
#site-viewport {
    position: relative;
    background-color: #086f61;
}
#mainnav li .mainnav-link, .parent-navbar li .mainnav-link {
    min-width: 80px;
    height: 41px;
    background-color: #086f61;
}
