css:
@-moz-document domain("progress.appacademy.io") {
body {
    background: #444
}


section,
.content {
    background: #191919 !important;
    color: #ddd;
}

input,
select,
textarea,
table {
    color: #ddd;
    background: #444 !important;
}

.scores th:nth-child(2),
.scores td:nth-child(2) {
    background: #555 !important;
}

.block,
article {
    background: #333 !important;
}

.standard-table tbody tr:hover {
    background: #333;
}

h2 {
    color: #ddd;
}
}
