css:
@-moz-document domain("web.telegram.org") {
.im_page_wrap {
    max-width: none;
}
.tg_head_split {
    max-width: none;
}

.footer_wrap {
    display: none;
}
.footer_empty {
    display: none;
}

.im_dialogs_col_wrap {
    max-width: 370px;
    padding-bottom: 0px;
}
.tg_head_logo_wrap {
    max-width: 370px;
}

.im_history_col_wrap {
    min-width: 69%;
    width: calc(100% - 370px);
}
.tg_head_main_wrap {
    min-width: 69%;
    width: calc(100% - 370px);
}

.im_message_wrap {
    max-width: none;
}
.im_send_panel_wrap {
    max-width: none;
    padding-bottom: 11px;
}
.im_send_form {
    max-width: none;
}

.im_edit_panel_wrap {
    margin: 0 12px 0 12px;
    padding-bottom: 31px;
}
}
