css:
@-moz-document regexp("https://dbatools.io/.*.") {
body, body p {
    background-color: #21252D;
    font-style: normal;
    text-transform: none;
    color: #fff
}

.blog .site-content, .single-post .site-content {
    margin-top: 0;
    background-color: #21252D;
}

a {
    color: #00a4ff;
}

a:hover {
    color: #2e6bf1;
}

h1, h2, h3 { 
    color: White;
}
}

