css:
@-moz-document domain(edmodo.com) {
#topbar,
.groups-container
  {
    background-color: #5c0226;
    color: #5c0226;
    border: none;
    box-shadow: 10px 10px 5px #488baf;
  }

  body
  {
     background: url(https://www.krasnapani.cz/frontend/data/files/clanky_listopad_16/stars-colorful-galaxy-space-far-1920x1080.jpg);
    min-height: 500px;
    /* Set background image to fixed (don't scroll along with the page) */
    background-attachment: fixed;
    /* Center the background image */
    background-position: center;
    /* Set the background image to no repeat */
    background-repeat: no-repeat;
    /* Scale the background image to be as large as possible */
    background-size: cover;
  }

  .span9
  {
    display: block;
  }

  .inner-panel
  {
    background-color: #b51148;
  }

  #notifications-list
  {
    background-color: #3d0020;
    color: #c20940 !important;
  }

  #notifications-list p
  {
    color: #b31111 !important;
  }

  .notification-reply-list,
.notification-reply-container,
.notification.new,
.notification.unread
  {
    background-color: #b90e44 !important;
  }

  .panel-title p
  {
    color: #d10f0f;
  }

  .notification .subtext strong
  {
    color: #ff0051;
  }

#component-app-quicklaunch,
#component-app-quicklaunch .apps-dock ul li,
.section-header-bg-white,
.pb-content,
.stream-content-header,
.sub-section-header
  {
    background-color: #730d34;
    color: white !important;
  }

  .group-message-count,
.link-list-group-threaded>li>div>a,
.link-list-group-options li a,
.section-header-bg-white
  {
    color: white !important;
  }

  .sunken
  {
    background-color: #77b6d708;
  }
}
