css:
@-moz-document url("http://mis.pafkiet.edu.pk/") {


#header-wrapper {
    padding: 2em 0 0 0;
    background-color:#831695;
}
#header {
    position: relative;
    background-color: #831695;
}
#features-wrapper {
    padding: 1.75em 0 1.75em 0;
    background-color: #f3e040;
}
.\31 2u, .\31 2u\24 {
    width: 100%;
    clear: none;
    background-color:#831695;
}
.box.feature {
    padding: 0;
    background-color: #FFF176;
}
a {
    color: #9C27B0;
    text-decoration: underline;
}
h1, h2, h3, h4, h5, h6 {
    color: #9C27B0;
    font-weight: 800;
}
ul.menu li:first-child {
    border-left: 0;
    margin-left: 0;
    padding-left: 0;
    color: #FFEB3B;
} 
}
