css:
@-moz-document url-prefix("https://www.sproom.net/en/"), url-prefix("https://www.sproom.net/da/"), url-prefix("https://sproom.net/da/"), url-prefix("https://sproom.net/en/") {
body {
	background-color: #383838 !important;
}

.backgroundForBodyAutorized,
.sp-private body {
	background: url(https://www.sproom.net/Content/images/new/background-pattern-dark.png);
}

.sp-shared-dialog {
	background-color: #636060;
	color: #d8d8d8;
}

.sp-side-menu-widget,
div.tabs a,
.leftSideMenu div a,
.leftSideMenu,
.sp-private .sp-side-nav-layout .sp-nav h3,
.sp-sproom-connector-dowload > .sp-slanted-corners-content,
.sp-nav a {
	color: #d8d8d8 !important;
}

.sp-slanted-corners-bottom,
.sp-slanted-corners-top {
	border-bottom-color: #636060;
	border-top-color: #636060;
}

.sp-bg-white {
	background-color: #636060;
}

.sp-company-logo .sp-slanted-corners-content {
	padding-bottom: 1px;
}

body > div.container > div.sp-main > div.row-fluid.sp-side-nav-layout > aside > div.sp-company-logo.sp-side-menu-widget > span.sp-slanted-corners-content.sp-bg-white > a > img {
	content: url(https://www.sproom.net/Content/images/sproom-logo.svg);
}
}
