css:
@-moz-document url-prefix("http://ictis.sfedu.ru/rasp") {

body {
    background: #232228;
    text-align: center;
}

td p font {
    color: #77BABC;
    font-size: 14;
    font-family: 'PT Sans', arial;
}

tr {
      font-family: 'PT Sans', arial;
    color: #8ad0d2;
}

table > tbody > tr:nth-child(1) > td > p > font {
    font-size: 18;
    font-weight: bold;
    color: #94fbff;
}

b {
    font-size: 16;
    color: #94fbff;
}

td {
    font-size: 18;
    padding: 5;
    color: #955d5d;
}


table {
    border-top-color: #848491;
    border-left-color: #848491;
    border-bottom-color: #848491;
    border-right-color: #848491;
    background: #23232D;
    margin: 0 auto;
}

 tbody > tr:nth-child(2) > td:nth-child(1) > p > font {
    font-size: 16;
    color: #955d5d;
    font-weight: bold;
     
}

body > p > font:nth-child(1){
    color: #5B8E90;
    font-family: 'PT Sans', arial;
}

body > p > font:nth-child(2){
    color: #1ca857;
    font-size: 24;
    
    font-family: 'PT Sans', arial;
}
}
