css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lifehacker.com") {
#sidebar, siteInfo, skySpacer, team, sidebarposts, weekly-subscribe {display:none !important;}
#content_container {background-color:#F8FAF0 !important; background-image:none !important;}
#footer {display:none;}
#header {display:none;}
#header_container {display:none;}
#menubar {margin-top:20px !important;}
.metadata {display:none !important;}
.excerpt {width:800px !important;}
#main {float:none !important;}
#content {width:1000px !important;} 
}
