css:
@-moz-document regexp("https:\\/\\/([Ww]{3}[.]{1})?thor\\.edu(?!\\/admin)(.*)") {
body {
    background-color: #222 !important;
}

div#block-system-main {
    background-color: #222 !important;
}
div#content div.block:not(.hasMasonry),
div#sidebar div.block,
div#content div.tiles div.head,
div.masonry-item,
div#content div.block.hasMasonry h2,
div#content div.block.hasMasonry div.view-header,
div#content div.hasGrid h2,
div#content div.hasGrid div.view-header,
.blockgrid .views-view-grid td,
.blockgrid .view-empty,
.views-responsive-grid .views-row,
div.white-bg,
div#content .views-responsive-grid .views-row .views-column,
div#footer,
#tablet_ads {
    background-color: #333 !important;
    color: silver;
}

div.view-sponsors div.masonry-item h3,
div.event p.title a {
    color: silver;
}

div#content div.messages.warning {
    background-color: #cecc33 !important;
}

div#navigation,
ul.menu li ul li,
.mean-container .mean-bar,
.mean-container .mean-nav,
.form-submit,
table caption,
.poll .bar .foreground {
    background-color: #800000 !important;
}

a {
    color: #F00;
    text-decoration: none;
}
}
