css:
@-moz-document url-prefix("https://allo.google.com/web") {
body {
    background-color: #212121;
}
.style-scope app-header-layout {
    background-color: #212121;
}

.style-scope conversations-view {
    background-color: #212121;
}


paper-item.conversation_item.layout.horizontal.style-scope.conversations-view {
    background-color: #212121;
    color: white;
}
.info.conversations-view h3.conversations-view {
    color: white;
}

.info.conversations-view #snippet.conversations-view {
    color: #a3a3a3;
}

.themedText {
    color: #838383;
}

.conversationHeader.wireball-app {
    background: #313131;
    color: white;
}

.fillParent.wireball-app {
    background-color: #212121;
    color: white;
}
}
