css:
@-moz-document url-prefix("https://stackedit.io/app#") {
.app--dark .markdown-highlighting .cl-hash {
    color: hsla(88, 50%, 53%, 0.8) !important;
}
.app--dark .markdown-highlighting .link .cl-underlined-text {
    color: hsla(262, 38%, 61%, 0.7) !important;
}
.markdown-highlighting .code{
    color: hsla(199, 86%, 65%, 0.7);
}

.app--dark .markdown-highlighting .cl, .app--dark .markdown-highlighting .hr{
    color: hsla(54, 100%, 62%, 0.7) !important;
}
.app--dark .markdown-highlighting .blockquote{
    color:rgb(147, 178, 163);
}

.markdown-highlighting .strong{
    color: hsla(0, 86%, 65%, 0.7);
}

code{
    background-color: rgba(89, 81, 30, .15);
    color: hsla(199, 86%, 65%, .83);
}
pre>code{
     background-color: rgba(89, 81, 30, .15);
}
}
