css:
@-moz-document domain("rbxtrading.com")
{
  html,
  	body {
    	background-image: url(https://DontChangeOrThemeBreaks.net);
	}
	img#site-logo.logo-small {
    	/* when in a thread, brighten up the butterfly logo to be about the main text color #AAA */
    	-webkit-filter: brightness(1.1);
    	background-image: url(https://i.imgur.com/ndMhIvD.png);
    	background-size: contain;
    	background-repeat: no-repeat;
    	object-position: -99999px 99999px;
	}

	img#site-logo.logo-big {
    	/* when in a thread, brighten up the butterfly logo to be about the main text color #AAA */
    	-webkit-filter: brightness(1.1);
    	background-image: url(https://i.imgur.com/6eUUkv0.png);
    	background-size: cover;
    	background-repeat: no-repeat;
    	object-position: -99999px 99999px;
	}
}
