css:
@-moz-document url-prefix("https://hitbtc.com/reports"), domain("api.hitbtc.com") {
/* Background */
div.content, 
div.content h1,
div.content > table {
	background-color: black!important;
	background: black!important;
}

/* Report Background */
div.wrap_report  {
	background-color: #454545!important;
	background: #454545!important;
}

/* Report Tabs */
div.wrap.wrap_fixed.wrap_report > div.container.container_fluid.container_report.clearfix > div > div > div > label {
	background: #325975;
	box-shadow: inset 0 0 17px 6px #325975;
}

/* Reports Page Background */
.atabs__contentItem {
	background: #414141;
	color: #fff;
}

/* My Order Table Header Background */
table.trades__table thead.trades__head.orders_history_header,
div.wrap.wrap_fixed.wrap_report > div.container.container_fluid.container_report.clearfix > div > div > div div > div > div > table > thead > tr > th,
div.wrap.wrap_fixed.wrap_report > div.container.container_fluid.container_report.clearfix > div > div > div > div.atabs__contentItem.atabs__contentItem_borderPadded.atabs__contentItem_report.adaptive_mobile_full.atabs__contentItem_visible > div > div.report__result > table > thead > tr > th > p,
div.wrap.wrap_fixed.wrap_report > div.container.container_fluid.container_report.clearfix > div > div > div > div.atabs__contentItem.atabs__contentItem_borderPadded.atabs__contentItem_report.adaptive_mobile_full.atabs__contentItem_visible > div > div.report__result > table > thead > tr > th > span{
	background: #414141;
	color: #4d8bb7;
}

/* My Order Table Cell Background */
div.wrap.wrap_fixed.wrap_report > div.container.container_fluid.container_report.clearfix > div > div > div > div div > div > div > table > tbody > tr > td,
div.wrap.wrap_fixed.wrap_report > div.container.container_fluid.container_report.clearfix > div > div > div > div.atabs__contentItem.atabs__contentItem_borderPadded.atabs__contentItem_report.adaptive_mobile_full.atabs__contentItem_visible > div > div.report__result > table > tbody > tr > td {
	background: #616161;
	color: #fff;
}







}
