css:
@-moz-document url-prefix("https://www.pushbullet.com/") {
.pushwrap {
  background: #222;
}

body {
  color: #777;
}
#header {
  background-color: #333 !important;
}

#mainbar {
  background-color: #222 !important;
}

.pointer {
  background-color: #222 !important;
}

.minitab pointer {
  background-color: #333 !important;
}

#sidebar {
  background: #222;
}

textarea,
input[type="text"],
input[type="password"],
input[type="datetime"],
input[type="datetime-local"],
input[type="date"],
input[type="month"],
input[type="time"],
input[type="week"],
input[type="number"],
input[type="email"],
input[type="url"],
input[type="search"],
input[type="tel"],
input[type="color"],
.uneditable-input {
  background: #222;
  color: #e6e6e6;
}

.pushfont-circle {
  color: green !important;
}

.chiclet {
  background-color: #555;
}

.hover-lightgray pointer {
  color: #e6e6e6;
}

.text-part {
  background-color: green;
}

#pushlist {
  background-color: #222 !important;
}
}
