css:
/* i really want this to be global */
body {
    background: #1d1d1d;
    color: #ffffff;
}
a {
    color: #358aed;
}

pre, code {
    background: #121212;
}

pre {
    border-left: 3px solid #358aed;
}

.token.atrule, .token.attr-value, .token.punctuation, .token.keyword {
    color: #358aed;
}

.token.property, .token.tag, .token.boolean, .token.number, .token.constant, .token.symbol {
    color: #c4006d;
}

code {
    border: 1px solid #272727;
}

h2 a, h3 a, h4 a, h5 a, h2 a:hover, h3 a:hover, h4 a:hover, h5 a:hover, h2 a:active, h3 a:active, h4 a:active, h5 a:active, h2 a:visited, h3 a:visited, h4 a:visited, h5 a:visited {
    color: #f9f9f9;
}

h1 img {
    -webkit-filter: invert(1);
    filter: invert(1);
}

th, td, tr {
    background-color: #272727;
    border-color: #959595
}
