css:
@-moz-document url-prefix("http://adam.curry.com/html/NoAgenda"), url-prefix("https://adam.curry.com/html/NoAgenda") {
body {
    background-color: #444;
    color:#CCC;
}
h1, h2, h3, h4 {
    color:#AAA;
}
.nav-tabs>li.active>a, .nav-tabs>li.active>a:hover, .nav-tabs>li.active>a:focus {
    background-color: #428bca;
    color:black;
}

}

@-moz-document regexp(".*disqus.com.*adamcurry.*") {
body {
    background-color: #444;
    color:#CCC;
}
h1, h2, h3, h4 {
    color:#428bca;
}
.tab-conversation.active>a {
    color:#428bca!important;
}
.discovery-main, 
.discovery-col-header h2,
.discovery-col-header h2 strong, 
.discovery-post .top-comment p,
.discovery-post .top-comment .user,
.discovery-post .meta {
    color:#AAA;
}
}
