css:
@-moz-document regexp('^https?:\\/\\/tools\\.ietf\\.org\\/html\\/.*') {
    html {
        background-color: #0e1112;
    }

    body {
        font-size: 16px;
    }

    * {
        color: #d4d7d6;
    }

    .bgblue {
        background-color: #1d5b91;
    }

    .docinfo {
        background-color: #6d8086;
    }

    .grey {
        color: #4d5a5e;
    }

    .grey,
    .grey a:link,
    .grey a:visited {
        color: #6d8086;
    }

    .top {
        border-top: 7px solid #d4d7d6;
    }

    .content {
        margin: 25px auto !important;
    }

    div[style="height: 13px;"] {
        display: none;
    }

    hr {
        width: 100% !important;
    }

    a.selflink {
        color: #6d8086;
    }
}
