css:
@-moz-document domain("cnbeta.com") {
/*样式调整*/
 html {
    background:#fff;
}
/*header*/
header.pageHeader{
	margin: 20px 0px;
   border-width: 0px;
}
nav.global-navi .nav-item a , .topSearchBox{
	border:none;
}

/*Slider*/
.topContentInner{
	background:#2B2D32;
}
/*Slider*/
 .topContentInner {
    background:#2B2D32;
}
/*body*/
 .main-wrap {
    background:#F4F4F5;
}
/*footer*/
 .pageFooter {
    background:#414347;
}
/*Sidebar*/
 .cnbeta-side-blue-title, .cnbeta-side-yellow-title {
    background:#2B2D32;
    color:#fff;
}
.hot-comments-plus {
    background-color:#414347;
    color:#ECEDED;
}

/*Hot.cnbeta.com*/
 .cnbeta-hot-welcome .cate-help {
    display:none;
}
.cnbeta-update-list{ width:100%; float:none;}
.cnbeta-update-list-article,.tks {
        display: none !important;
        /*新闻右侧相关新闻推荐*/
    }

.cnbeta-update-list .item dl {

    padding: 20px 20px 10px 150px;
}
.cnbeta-update-list .item .meta-data .status{
right:20px;
}

/*去广告*/
.cnbeta-article-latest,.cnbeta-home-side{display:none;}
.cnbeta-article{ float:none; margin:0 auto; width:1200px;}
.tbl-feed-container,.trc_rbox_div{display: none !important;}
}
