css:
@-moz-document domain(jira.idco.io) {
    .ghx-issue-compact .ghx-summary {
        overflow: hidden;
        direction: rtl;
    }

    #summary-val {
        direction: rtl;
    }

    .ghx-issue-fields .ghx-summary .ghx-inner {
        display: block;
        line-height: 1.5;
        max-height: 3.3em;
        overflow: hidden;
        word-wrap: break-word;
        direction: rtl;
    }

    .details-layout .split-view .list-results-panel .list-panel .issue-list > li .splitview-issue-link .issue-link-summary {
        direction: rtl;
    }

    body {
        font-family: IRANSans, IRANSansWeb, Tahoma, Arial, 'Comfortaa', cursive;
        font-weight: 400;
    }
}
