css:
@-moz-document domain(euractiv.com) {
:root {
    --color-font: #ddd;
    --color-font2: #bbb;
    --color-borders: #666;
}

#ea-ancillary-menu,
nav#ea-primary-menu{
    background: #222;
    border-bottom: 1px solid var(--color-borders) !important;
}

body,
.modal-content,
.thumbnail,
ol#ea-breadcrumb.breadcrumb{
    color: var(--color-font) !important; 
    background: #333;
}

ul.ea-content-hint li,
.ea-nav-stacked li a,
.table > tbody > tr > td, 
.table > tbody > tr > th, 
.table > tfoot > tr > td, 
.table > tfoot > tr > th, 
.table > thead > tr > td, 
.table > thead > tr > th{
    border-color: var(--color-borders);
}
#ea-ancillary-menu a,
.ea-card .thumbnail h3 a,
.thumbnail .caption,
.ea-article-body-content div.ea-own-embed .own-embed-excerpt a:link, 
.ea-article-body-content div.ea-own-embed .own-embed-excerpt a:visited,
.ea-sidebar h3,
.ea-sidebar h3 a:link, 
.ea-sidebar h3 a:visited,
.pagination-link a,
text tspan,
.ea-nav-inline li a{
    color: var(--color-font) !important; 
}

.ea-card .thumbnail-meta a, 
.ea-card .caption a,
.single .ea-image-meta,
.breadcrumb > .active {
    color: var(--color-font2) !important; 
}

.ea-native article.ea-article, 
li.ea-native-card, 
.ea-native-card .thumbnail,
.single .ea-image-meta,
input,
.modal-content.elections{
    background: #444 !important;
    border-color: var(--color-borders) !important;
}

.ea-article-body a:link, 
.ea-article-body a:visited,
ol#ea-breadcrumb.breadcrumb a:link,
ol#ea-breadcrumb.breadcrumb a:active, 
ol#ea-breadcrumb.breadcrumb a:hover, 
ol#ea-breadcrumb.breadcrumb a:visited,
.ea-article-header p.ea-byline a:link, 
.ea-article-header p.ea-byline a:visited,
.ea-sidebar a:link, 
.ea-sidebar a:visited,
.table a{
    color:#09b !important;
}
img.ea-media-formatted, 
[class^="ea-image-aspect-"], 
[class*=" ea-image-aspect-"]{
    background-color:#444;
}

#ea-ancillary-menu button {
    filter: invert(.8);
}
}
