css:
@-moz-document url-prefix('https://www.liquidation.com/aucimg//'),
url-prefix('https://www.liquidation.com/aucimg/') {

table table tr td {
    padding:15px;
    border:0px solid transparent;
}

table table * {
        font-family:Verdana;
}

table table tr:nth-child(odd) {
    background-color: #eee;
}

table table tr:nth-child(even) {
    background-color:#fff;
}

table table tr:nth-child(1) {
    background-color:#444;
    color:#fff;
}

table table tr:nth-last-child(1) {
    background-color:#17421c;
    color:#fff;
    font-size:1.4em;
}

table table tr:nth-last-child(1) td:nth-child(4):first-letter {
   color:rgba(255, 255, 255, 1);
    vertical-align: super;
    font-size:.6em;
    padding-right:2px;  
}


table table tr:not(:nth-child(1)) td:nth-child(3), table table tr:not(:nth-child(1)) td:nth-child(4) {
    font-size:1.3em;
}

table table tr:not(:nth-child(1)):not(:nth-last-child(1)) td:nth-child(3):first-letter, table table tr:not(:nth-child(1)):not(:nth-last-child(1)) td:nth-child(4):first-letter {
   color:rgba(0, 0, 0, .5);
    vertical-align: super;
    font-size:.6em;
    padding-right:1px;  
}
}
