css:
@-moz-document url-prefix("https://www.indeed.com/jobs") {
#resultsCol .result {
    background-color: lightgray;
    transform: scale(0.8)
}
#resultsCol .row.result[data-tn-component="organicJob"] {
    background-color: #fff;
    transform: scale(1);
    padding: 16px 12px !important;
}
.sj1, .sll, .slc, .sjas1, #auxCol {
    display:none !important
}
table#pageContent {
    max-width: 1200px;
}
#resultsCol {
    max-width:750px !important;
    width:750px !important
}

}
