css:
@-moz-document domain('scenerules.org')
{
    html {
        background-color: #0e1112;
    }

    body {
        font-size: 14px;
        color: #d4d7d6;
        line-height: 1.5em;
    }

    h1 {
        font-size: 18px;
    }

    h2 {
        font-size: 16px;
    }

    a {
        padding: 0 5px;
        color: #e37933;
    }

    a:visited {
        color: #b55421;
    }

    input{
        color: #d4d7d6;
        background: #090b0d;
        border: 1px solid #b55421;
    }

    .nfo {
        display: flex;
        font-size: 16px;
        justify-content: center;
    }
}
