css:
@-moz-document domain("shui5.cn") {
	/*屏蔽百度页内搜索*/
	div.baiduSearch,
    div.fullScreenAd.container.hidden-xs,
	div.pica {
		display: none !important;
	}
	.navbar {
		font-size: 1em !important;
	}
	.ul {
		font-size: 20.4px !important;
	}
	#sidebar,
	#footerBg,
	.nav > li:nth-child(12) > a:nth-child(1),
	.deansubnav {
		display: none !important;
	}
	.col-sm-8 {
		width: 100%;
	}
	.arcList ul li p {
		font-size: 20px;
		float: left;
		height: 32px;
		line-height: 32px;
		overflow: hidden;
		width: 100%;
	}
	.articleTitle h1 {
		font-size: 34.5px !important;
	}
	.articleDes,
	.articleResource {
		font-size: 16.5px !important;
	}
	.arcContent p,
	.arcContent span,
	.arcContent td,
	th {
		font-size: 19.5px !important;
		color: rgba(34, 34, 30, 0.94);
		border-color: rgb(130, 127, 122) !important;
	}
	.vsc-initialized,
	body {
		background: none;
		background-color: #f3f2ee !important;
	}
	a {
		color: rgba(34, 34, 30, 0.94) !important;
	}
}
