css:
@-moz-document domain("threadreaderapp.com") {
body {
    background: #000;
    color: #fff;
}

.container .unroll {
    color: #f4f4f4;
    background: #575553;
}

.container .unroll a,
.container .unroll a.button.button-main-action {
    color: #fff;
}

.container .unroll a.button {
    color: #000;
}

.box-error, .box-support {
    border-color: #e8d35c;
    color: #fff4af;
    background: #000;
}
}
