css:
@-moz-document url(https://www.sefaria.org.il/) {
	
    * {
        color: #fff;
        background-color: #333 !important;
    }
    
    a *, 
    a {
        color: #e8dfa9;
        background-color: #222 !important;
    }
    a:hover *, 
    a:hover {
        color: #e8dfa9;
        background-color: #444 !important;
    }
    
    .basetext .segment.highlight, .sheetContent .segment.highlight .en, .sheetContent .segment.highlight .he, .readerPanel.bilingual.heLeft .sheetContent .segment.highlight, .readerPanel.bilingual.heRight .sheetContent .segment.highlight {
        background-color: #ead698;
    }

.segment .refLink {
    font-size: .8em;
    font-weight: bold;
    letter-spacing: 1px;
    cursor: pointer;
    color: #f3f051;
    padding: 3px 8px;
    border-radius: 14px;
}
    }
