css:
@-moz-document domain("lmsnovel.wordpress.com") {
/* ==UserStyle==
@name        Dark theme for lmsnovel
@namespace   github.com/openstyles/stylus
@version     1.0.2
@author      jlneder <jlneder@gmail.com>
@license     unlicense
==/UserStyle== */

/* Main background and font color */
body,
.hfeed.site {
    background-color: #1a1a1a !important;
    color: #eee !important;
}

.hfeed.site a {
    color: #eee !important;
}


/* Text/Reading Area */
main.site-main {
    background-color: #1a1a1a !important;
    color: #eee !important;
}

.hentry,
header.entry-header,
div.entry-content {
    background-color: #232323 !important;
    font-family: 'Open Sans', sans-serif !important;
}


/* Menu Bar */
.main-navigation,
.navigation,
.post-navigation,
.nav-links {
    color: #eee !important;
    background-color: #232323 !important;
}


/* Title */
.entry-title,
.entry-title a {
    color: #eee !important;
}


/*Removed the sidebar */
#secondary {
    display: none !important;
    opacity: 0 !important;
}

main.site-main {
    margin: 0 15%;
}
@media only screen and (max-width: 600px) {
    main.site-main {
        margin: 0 10px;
    }
}


/*Removed infinite footer */
#infinite-footer {
    display: none !important;
    opacity: 0 !important;
}

}
