body, td {
    color: #9090ad;
    background-color: #2c303c ;
}
a:link, a:visited { color:#5858ff }
#puzzle_grid td, input {
    background-color: #babace ;
}
#message font {
    color: #fb55a8;
}

.d0,.d1,.d2,.d3 {color:#9542ff;}
.d1,.s1,.u1,.w1,.v1,.c1,.e1,.f1,.g1,.h1,.i1,.j1,.k1,.l1,.m1,.n1,.p1,.q1,.r1 {background-color: #ffa8b5;}
.d2,.s2,.u2,.w2,.v2,.c2,.e2,.f2,.g2,.h2,.i2,.j2,.k2,.l2,.m2,.n2,.p2,.q2,.r2 {background-color: #f78899;}
.d3,.s3,.u3,.w3,.v3,.c3,.e3,.f3,.g3,.h3,.i3,.j3,.k3,.l3,.m3,.n3,.p3,.q3,.r3 {background-color: #fb415d;}
.v0,.v1,.v2,.v3,.w0,.w1,.w2,.w3 {color:#23844f;}

.stat, .reset {
    color: #b18fff;
}
}
