css:
@-moz-document url-prefix("http://www.lispworks.com/documentation/HyperSpec"), url-prefix("http://clhs.lisp.se/"), url-prefix("http://www.lispworks.com/documentation/lw50/CLHS"), url-prefix("http://www.ai.mit.edu/projects/iiip/doc/CommonLISP/HyperSpec"), url-prefix("http://www.lispworks.com/documentation/lw71/CLHS") {
:root {
    --nord0: #2E3440;
    --nord1: #3B4252;
    --nord2: #434C5E;
    --nord3: #4C566A;
    --nord4: #D8DEE9;
    --nord5: #E5E9F0;
    --nord6: #ECEFF4;
    --nord7: #8FBCBB;
    --nord8: #88C0D0;
    --nord9: #81A1C1;
    --nord10: #5E81AC;
    --nord11: #BF616A;
    --nord12: #D08770;
    --nord13: #EBCB8B;
    --nord14: #A3BE8C;
    --nord15: #B48EAD;
}



body,html {
    background: var(--nord0) !important;
    color: var(--nord4) !important;
    font-family: 'Linux Libertine' !important;
    font-size: 14pt;
}

pre {
    background: var(--nord1) !important;
    color: var(--nord5) !important;
    font-family: 'Source Code Pro' !important;
    font-size: 14pt;
}

a:link {
    color: var(--nord8) !important;
}

a:visited {
    color: var(--nord15) !important;
}

img {
   background-color: var(--nord6); 
}
}
