css:
@media only screen and (max-width: 700px) {
    body {
        width: 480px;
        background-color: #303438;
    }
    input[type="text"] {
        background-color: #4c5257;
        color: white;
        font: small-caption;
    }
    select {
        background-color: #4c5257 !important;
        color: white;
    }
    input::placeholder {
        color: white;
    }
    .file-header {
        background-color: #414447 !important;
        color: white;
    }
    .container {
        width: 480px;
        left: 0px;
        position: fixed;
        top: 150px;
    }
    .footer {
        display: none;
    }
    .gist-welcome {
        display: none;
    }
    .cm-string {
      color: green; 
    }
    .CodeMirror-lines, .CodeMirror-scroll, .CodeMirror-sizer, .CodeMirror-gutter, .CodeMirror-gutters, .CodeMirror-linenumber {
        background-color: #2f2e2e !important;
        color: white;
    }
    .cm-s-github-light.CodeMirror {
        background-color: #2f2e2e !important;
        color: white;
    }
    .CodeMirror pre {
        background-color: #2f2e2e !important;
        color: white;
    }
}
