css:
@-moz-document url("https://omni.se/"), domain("omni.se") {
body,
.app-bg,
.group,
.article,
.component,
.resource,
.article-ago,
.article--compact .resource--text:after,
.btn--light{
    background: #212121 !important;
    background-color: #212121 !important;
    color: white !important;
}

.article--sponsored .resource--title {
    color: #d0d3a9 !important;
}

.group,
.box-heading {
    border-bottom: 8px solid #212121;
}
.component--topicBox--storyHeading > a {
    background: #212121 !important;
}

.header,
.menu  {
    background-color: #111111 !important;
}

.topnav a,
.search,
.search input {
    background: #212121 !important;
    background-color: #212121 !important;
    color: white !important;
}

.topnav,
.article--sponsored::before {
    background: none;
}

.usermix-category-option-label {
    background: #212121;
}
}
