@-moz-document domain("cds.rosneft.ru") {

/* уменьшаем весь интерфейс*/
body {
 zoom: 0.9}	

/**/
#mainDiv > mat-sidenav-container > mat-sidenav-content {
	height: 1020px !important
}
  
/* Отключаем всплывающее меню слева*/
#mainDiv > mat-sidenav-container > mat-sidenav {
	display: none
}

 /* Боремся с переносами в Ленте*/
.sng-chat__message-content {
	display: flex !important
}

.sng-chat__message__content_header,
.orange-text.sng-chat__message-tooltip-target {
	width: 220px !important
}

.sng-chat__message[_ngcontent-c21] {
	margin-top: 2px !important
}


.sng-chat__message__content_body__message {
	width: 100% !important
}
  
  /*Меняем шрифт*/
html {
	font-family: Tahoma !important
}

/*Обращение. уменьшаем высоту заголовков*/
.group-line {
	height: 1.9em !important
}

/*Обращение. Оформление активной вкладки меню "Детали, Аудит и т.д."*/
.mat-tab-label.mat-ripple.mat-tab-label-active {
	color: #3f51b5;
	font-weight: bold;
	opacity: 1;
}

/*Обращение. Уменьшаем расстояние в выпадающих списках */
.mat-option {
	height: 1.15em !important
}

/*Обращение. Уменьшаем отступы между блоками боковая панель свёрнута*/
.sng-sidenav2__content__view {
	padding-top: 4.6em	
}
.layout__left .layout__form {
	margin-right: 0.5em;
}
.mat-card.block-default {
	padding: 0.3em !important
}

/*Обращение. Поджимаем все поля*/
.mat-form-field-wrapper {
	padding-bottom: 0.01em !important
}

/*Обращение. Скрываем горизонтальный пунктир */
div.mat-form-field-underline,
div#classification_classification {
	display: none !important
}

.dxeButtonEdit_XafTheme {
	border: none !important
}

.form-item,
.form-item-control,
.sng-checkbox,
.sng-control__container {
	padding-top: 0px;
	padding-bottom: 0px !important
}

/*Обращение. Раскрашиваем поля ввода*/
.mat-form-field,
.dxeMemoEditAreaSys,
input[type="text"].dxeEditAreaSys,
input[type="password"].dxeEditAreaSys {
	border: 1px groove !important
}

/*Обращение. SC0000161512 Размер темы обращения*/
#info_subject {
	color: #3f51b5;
	font-size: 1.3em;
}

/*Обращение. Полужирный для выпадающих меню и номера обращения*/
.mat-option-text,
.title__bottom {
	font-weight: 600;
}


/*Обращение. Полужирный текст в полях*/
.mat-input-element.mat-form-field-autofill-control.cdk-text-field-autofill-monitored {
	font-weight: 600;
}

/*Обращение. Постоянный показ и увеличение кнопок просмотра и добавления карточки инициатора*/
.fas.fa-plus-circle.field-item.ng-star-inserted,
.fas.fa-search.field-item.ng-star-inserted {
	font-size: 20px;
	margin-top: -6px
}

/*Обращение. Лента. Цвет активной кнопки отправки комментария*/
.mat-button__sm.mat-raised-button:enabled {
	background: green;
	color: white;
}

/*Обращение. Лента. Приближаем кнопку Отправить к левой части*/
.sng-rich-edit__button-right[_ngcontent-c45],
.sng-rich-edit__add-btn-wrapper[_ngcontent-c45],
.wd-block.wd-2.sng-rich-edit__add-btn-wrapper,
.sng-rich-edit__button-right {
	flex-direction: row !important
}

.wd-block.wd-10,
.wd-2 {
	width: 50% !important
}

/*Обращение. Лента: Рамка вокруг поля отправки коменнтария*/
.sng-rich-edit[_ngcontent-c34] {
	padding: 0.3em;
	max-width: 1600px !important
}


/*Обращение. Лента. Разрешение копирования автора комментария*/
.orange-text.sng-chat__message-tooltip-target {
	-moz-user-select: auto;
	-webkit-user-select: auto;
}

/*Обращение. Лента. Сдвигаем блок с добавлением новой записи влево */
#mainDiv > mat-sidenav-container > mat-sidenav-content > div.sng-sidenav2__content__view > app-service-desk-request-detail2 > div > div > div.layout__right.ng-star-inserted > div.layout__right_bottom > sng-service-desk-request-tab > div > div > sng-detail-tab-ribbon > div > mat-card > mat-card-content > div.group-line > hr {
	
width: 10% !important
}

/*Правая панель кнопок. Увеличение расстояния между кнопками 2019
.sng-button.mat-raised-button {
	margin-top: 1.5em
} 
*/

/*Правая панель кнопок. предоставить решение и взять в работу - разукрашиваем
#mainDiv > mat-sidenav-container > mat-sidenav-content > div.btn-menu > sng-btn-menu > div > sng-service-desk-request-detail-btn-menu > div > div:nth-child(9) > button,
#mainDiv > mat-sidenav-container > mat-sidenav-content > div.btn-menu > sng-btn-menu > div > sng-service-desk-request-detail-btn-menu > div > div:nth-child(7) > button {
	background-color: #00912626;
	font-weight: 600;
	font-size: 1.1em;
	height: 4em;
	}
*/
/*Правая панель кнопок. Обводка кнопок
.sng-button.mat-raised-button,
.list-view-grid__bottom-line__btn-clean-filter.mat-raised-button,
.sng-dialog-ok__btn.mat-raised-button {
	border: 1px solid #aaaaaa;
} 
*/

/*Обращение. Кнопки ластик, просмотр и добавления карточки инициатора: Эффект наведения*/
.fas.fa-plus-circle.field-item.ng-star-inserted:hover,
.fas.fa-search.field-item.ng-star-inserted:hover,
.fas.fa-eraser.field-item.ng-star-inserted:hover {
	opacity: 1
}

.fas.fa-plus-circle.field-item.ng-star-inserted,
.fas.fa-search.field-item.ng-star-inserted,
.fas.fa-eraser.field-item.ng-star-inserted {
	opacity: .4;
}

.dx-dropdowneditor-icon:hover {
	opacity: 1
}

/*Обращение. Подкрашиваем поле крайний срок*/
mat-form-field#material_detailes_dateUse {
	background-color: #ff000026 !important
}

/*Обращение. Сдвиг надписей в полях вниз - откл*/
input.mat-input-element {
	margin-top: 0.29em;
}

/* Обращение. Выпадающий список исполнителей. Скрываем организацию */
#mat-autocomplete-19.sng-mat-option__org,
#cdk-overlay-7.sng-mat-option__org,
#cdk-overlay-1.sng-mat-option__org {
    display: none
}
/* Обращение. Выпадающий список исполнителей. Скрываем должность */
#mat-autocomplete-19.sng-mat-option__job,
#cdk-overlay-1.sng-mat-option__job,
#cdk-overlay-7.sng-mat-option__job {
    display: none
}

/*Журнал. Выделяем счётчик в строке итогов*/
.dx-info {
	font-size: 1.1em;
	font-weight: bold !important
}


/*Журнал. Опять убираем отступы*/
.mat-card,
.mat-form-field-infix {
	padding: 0px !important}

/*Журнал. Высота списка представлений*/
.ng-trigger.ng-trigger-transformPanel {
	margin-top: 5em;
	min-height: 5em;
	max-height: 40em}

/*Журнал. Список представлений. Подправляем */
div.ng-trigger.ng-trigger-transformPanel.ng-tns-c16-5.mat-select-panel.mat-primary.ng-star-inserted {
	margin-left: 14em !important;
	}
#mainDiv > mat-sidenav-container > mat-sidenav-content > div.sng-sidenav2__content__view > ng-component > div > mat-card > mat-card-content > sng-list-view-grid > div > div > div.form-item {
	padding-left: 0px !important
}

/*Ошибка */
.sng-notification_error,
.sng-notification__content, 
.sng-notification__subject.ng-star-inserted, 
.sng-notification__text[_ngcontent-c10] {
	width: 70em !important
}


/*Журнал. Закрепляем строку итогов внизу 31/12/19*/
div.dx-datagrid-pager.dx-pager {
	position: fixed;
	height: 1.7em;
	width: 40%;
	background-color: #eeededfc;
	left: 75em;
	bottom: 0.1em;
	padding: 0px;
	margin: 0em;
	border-width: 1px !important;
	border-color: black !important
}

/*Журнал fix июльского релиза 2020*/
.list-view-component[_ngcontent-c11], .sng-sidenav2__content__view.ng-star-inserted {
    padding: 1px;
    height: calc(100vh + 1em)  
}

/*Правим стить строки итогов 31/12/19*/
.dx-page,
.dx-page-sizes, 
.dx-page-size, 
.dx-pages, 
.dx-pager
{
	padding: 3px !important
}

.dx-datagrid-pager.dx-pager 
{
	padding: 0 !important
}

/*скрываем поле списание за день 2020*/
sng-staff-time.staff-time {
	display: none !important
}
	
/*Поле поиска уменьшаем 2020*/
.sng-navbar-header__right__search {
	width: 250px !important
}
	
/*Обращение. Меню код решения. Выделяем Решено */
div.cdk-overlay-2 {
	background-color: #00800066
}

/* Обращение. скрываем классификацию 2020*/
#mainDiv > mat-sidenav-container > mat-sidenav-content > div.sng-sidenav2__content__view > app-service-desk-request-detail2 > div > div > div.layout__left.ng-star-inserted > div > mat-card > mat-card-content > sng-detail-block-classification > div > div:nth-child(7),
#Vertical_v13_13133790_MainLayoutEdit_xaf_l389_xaf_l389_CardTable > tbody > tr:nth-child(2) > td > div:nth-child(11) {
	display: none !important
}
/*Общее. Расширяем поле просмотра*/
.sng-sidenav2__content,
.sng-sidenav2__content__view {
	margin-right: 2px !important;
	margin-bottom: 0px !important
}
 
}

@-moz-document url-prefix("https://cds.rosneft.ru/client/listview/") {
/*Журнал. кнопки поднимаем вверх и в линию*/
.layout__buttons {
	position: fixed;
	-webkit-box-orient: horizontal;
	flex-direction: row;
	top: 1px;
	right: 39.2%;
	width: 49%;
	height: 1.2em;
	border: 0px;
	background-color: white
}
}

@-moz-document url-prefix("https://cds.rosneft.ru/client/sdesk/") {
/*Обращение. Кнопки поднимаем вверх и в линию*/
.layout__buttons {
	position: fixed;
	-webkit-box-orient: horizontal !important;
	flex-direction: row-reverse !important;
	top: 1px !important;
	right: 19.2% !important;
	width: 70%;
	max-width: 1450px !important;
	height: 1.2em !important
}

/*скрываем поле списание за день*/
sng-staff-time.staff-time {
	display: none !important
}

/*Скрываем Сохранить и закрыть*/
button.sng-button.sng-button__bottom.mat-raised-button.ng-star-inserted {
	display: none !important
}

/*Кнопку перезагрузить перемещаем
div.refresh {
	position: fixed !important;
	top: 50px !important;
	right: 10px !important;
	background-image: url(/client/assets/images/plus.png)	
}
*/
}

@-moz-document url-prefix("https://cds.rosneft.ru/client/list/individuals/") {
/*Физ лица. кнопки поднимаем вверх и в линию*/
.layout__buttons {
	position: fixed;
	-webkit-box-orient: horizontal;
	flex-direction: row;
	top: 1px;
	Right: 35%;
	width: 15%;
	height: 1.2em;
	border: 0px;
	background-color: white
}
}

@-moz-document url-prefix("https://cds.rosneft.ru/client/individual/details/") {
/*Физ лица. кнопки поднимаем вверх и в линию*/
.layout__buttons {
	position: fixed;
	-webkit-box-orient: horizontal;
	flex-direction: row;
	top: 1px;
	Right: 35%;
	width: 23%;
	height: 1.2em;
	border: 0px;
	background-color: white
}
}
