css:
@-moz-document domain("app.roll20.net")
{
.content {
    /*font-family: calibri; */
    font-size: 1.3em;
  }
  p {
    font-size: 1.0em;
  }
  h4 {
    font-size: 1.05em;
    font-variant: small-caps;
  }
  h3, h2 {
    font-variant: small-caps;
  }

  .sheet-page.sheet-core
  {
    min-width: 800px;
  }

  .charsheet .sheet-header-info
  {
    width: 495px;
  }

  .charsheet .sheet-header
  {
    height: 95px;
    min-width: 800px;
  }

  .charsheet .sheet-ac-init-speed-container div
  {
    height: 60px;
  }

  .charsheet .sheet-ac-init-speed-container, .charsheet .sheet-hp, .charsheet .sheet-hdice-dsaves-container, .charsheet .sheet-attacks, .charsheet .sheet-equipment
  {
    margin-bottom: 25px;
  }

  .charsheet .sheet-body
  {
    margin-left: 17px;
  }

  .charsheet .sheet-attr-container
  {
    width: 85px;
    left: -17px;
  }

  .sheet-row-container>input[type="checkbox"]
  {
    transform: scale(0.7);
    margin-right: -5px;
   }
}
