css:
@-moz-document url-prefix("https://www.reddit.com/login"), url-prefix("https://www.reddit.com/password"), url-prefix("https://www.reddit.com/register") {
/* Login page by RHIN0X */
.PageColumns {
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    overflow-x: hidden;
    min-height: 100vh;
    background-color:#202020;
}
input {
    background-color: #161616 !important;
    color:#cecece;
}
.AnimatedForm__submitButton{
    background:#252525;
}
.AnimatedForm__submitButton:hover{
    background:#505050;
}
.BottomText .BottomLink {
    font-size: 12px;
    line-height: 24px;
    color:#cecece;
}
/* Password page by RHIN0X */
.Description, p {
    font-family: Noto Sans,sans-serif;
    font-size: 14px;
    font-weight: 400;
    line-height: 21px;
    color:#cecece;
}
.BottomText {
    color:#cecece;
}
/* Register page by RHIN0X */
.AnimatedForm__nextButton{
    background:#252525;
}
.AnimatedForm__nextButton:hover{
    background:#505050;
}
.AnimatedForm__bottomNav{
    background-color:#161616;
}
}
