css:
@-moz-document domain("matemaks.pl") {
/*Matemaks.pl - Czarna wersja kolorystyczna - zrobiona hobbystycznie - By PACIK19*/
body {
    background: #474747 !important; 
}

.container{
    box-shadow: 100px 0 180px -80px #000, -100px 0 180px -80px #000
}

.middle{
    background: #0c0c0c;
    box-shadow: 0 0 20px #000000 inset
}

.footer a:link, .footer a:visited
{
    color: #888888
}

.news{
    color: #666
}

.news span{
    color: #0b460f;
}

.aktualnosci .news a:link {
    color: #0b6680
}

.aktualnosci .news a:visited {
    color: #999
}

.glowne_linki a, .menu_wysuwane a{
    background: #050505;
    color: #666;
    box-shadow: none;
}

.tresc{
    color: #666;
}

.twierdzenie, .definicja, .twierdzenie1, .algorytm, .program{
    background-color: #050505;
}

.lekcja{
    background-color: #050505;
}

.pasek_root{
    background-color: #050505;
    color: #666;
}

.but.b_prem{
    background-color: #050505;
}

a.but:link, a.but:visited, a.but:hover{
    color: #605e3a;
}


.pasek_root_2{
    background-color: #050505;
}

.fb_iframe_widget_fluid_desktop, .fb_iframe_widget_fluid_desktop span, .fb_iframe_widget_fluid_desktop iframe {
    background-color: #090909;
}
}
