css:
@-moz-document domain("typeracerdata.com") {
body {
    background:#111!important;
    color:#999;
}

h1, h2, h3, h4, h5, h6 {
    color:#999;
}

#nav {
    border-bottom: 1px solid #313131!important;   
}

table th {
    background-color: #111!important;
    color:#999;
}

table td {
    background-color: #111!important;
    color:#999;
}

.stats th, .stats td {
    background-color: #111!important;
    color:#999;
}

table a{
    background-color:#111!important;
    color:#999;
}

.stats th, .stats td, .stats r.sort{
    border: 1px solid #313131!important;
}

.profile th, .profile td {
    border: 1px solid #313131!important;
}

input {
    border: 1px solid #313131!important;
    color:#999;
}
}
