css:
@-moz-document url-prefix("http://live.nicovideo.jp/search?") {
.search-result-context-area {
    display:none!important;
}

.result-list {
    flex-flow: row wrap;
    padding-top:0px!important;
    margin-top: -12px!important;
}

.result-item {
    width:490px!important;
    padding-right: 14px!important;
}

.timeshift-button-label {
    font-size:0px!important;
}
.result-item .timeshift-reserve-button>.timeshift-button-label:before {
    margin-right:0px!important;
}

.result-item:nth-child(1) {
    padding-top: 12px!important;
    margin-top: 8px!important;
}

.result-item+.result-item:nth-child(2) {
    border-top:0px!important;
}

.result-item>.program-summary-area>.provider-area {
    padding-bottom:0px!important;
}


}
