css:
@-moz-document domain("github.com") {
.user-following-container button.js-user-profile-follow-button {
    /* Follow */
}

.user-following-container.on button.js-user-profile-follow-button {
    /* Unfollow */
    font-size: 0;
    line-height: 0;
}

.user-following-container.on button.js-user-profile-follow-button:after {
    font-size: 14px;
    line-height: 20px;
    content: 'Following';
}

.user-following-container.on button.js-user-profile-follow-button:hover {
    background: #cb2431;
    color: #fff;
}

.user-following-container.on button.js-user-profile-follow-button:hover:after {
    content: 'Unfollow';
}

}
