css:
@-moz-document domain("airbrake.io") {
body,
body.settings-view {
    background-color: #3f3f3f;
    color: white;
}
.dashboard,
.groups-panel.panel,
.project-detail,
.ab-card,
.ab-card__data,
a.dash-list-item,
.ab-search,
.group-block .minor-text,
.empty-state,
.settings-view-layout,
.settings-view-layout .pane-content .pane-inner,
.settings-view-layout .pane-content .pane-inner .settings-title,
.integration-title,
input {
    background-color: #3f3f3f;
    color: white;
    border-color: white;
}
.pill,
.groups-panel .panel-heading .groups-multi-select,
.form-control,
.form-control[disabled],
.form-control[readonly],
fieldset[disabled] .form-control,
pre,
.btn:hover,
a.dash-list-item:hover,
.group-block:hover {
    background-color: gray;
    color: white;
    border-color: white;
}
a {
    color: orange;
}
a:hover,
.active .ab-tabs__link,
.active .ab-tabs__link:hover,
.active .ab-tabs__link:focus {
    color: green;
}
.axis text,
.legend text {
    fill: white;
}
}
