    background-color: RGB(246, 241, 231);
    width: 800px;
}
.footer_link {
    display: none;
}
.reader_mark0, .reader_mark1, #page_set, .bottem1 {
    display: none;
}
.bottem{
    border-top: none;
}
.bookname{
    margin-top: 40px;
    color: #262626 !important;
    width: 76%;
}
.bookname h1 {
	text-align: left;
}

}
