css:
@-moz-document domain("1klik.hr") {
/* v1.0 by jamshy */

a[class="hide_mobile"] {
    display:none
}
header .logo {
    height: 42px;
    width: 190px;
    margin-right: 40px;
    margin-left: 10px;
    background: url(https://1klik.hr/wp-content/themes/main/assets/img/img_footer_logo.png);
    background-repeat: no-repeat;
    background-size: 100% 100%;
}
body {
    background-image: none;
    background-color: #191a1e;
    color: white;
}
.bg-img-active .home.container {
    background-color: #282b30;
    padding: 10px;
}
a {
    text-decoration: none;
    color: white;
}
header .navi {
    float: left;
    width: 100%;
    background-color: #333;
}
header ul.main_navigation li a {
    background-color: #333;
}
.bg-img-active header {
    background-color: #000000;
}
.submenu_links {
    background-color: #000000;
}
.submenu_links .tab li a {
    color: #fff;
    font-size: 13px;
}
.submenu_links .tab h2 {
    color: #fff;
}
.box-row li .time p {
    color: #c7cbd1;
}
.midas-network-article-horizontal-responsive a {
    color: #FFF;
    text-decoration: none;
}
.box-row.style3 a {
    color: #FFF;
}
.midas-network-article-horizontal-responsive1 a {
    color: #c7cbd1;
}
.midas-network-article-horizontal-responsive1 {
    color: white;
}
.bg-img-active .subcategory.container {
    background-color: #282b30;
    padding: 20px;
}
.box-row.style4 .content-small>p {
    font-size: 14px;
    line-height: 21px;
    color: #c7cbd1;
}
aside .inner {
    border: none;
    background: #333;
}
.full-width .list_subcategory {
    background-color: #333;
}
.radio-list {
    background-color: black;
}
}
