css:
@-moz-document domain("w3resource.com") {

html,body {
   background:#000!important;
}

article {
  background: #000!important;
  color: #09f!important;
}

.mdl-card {
   background:#000!important;
}

.article-tab {
    background: #000;
    color: #fff !important;
border:0px !important;
}

.article-tab p {
    background: #000;
    color: #fff !important;
border:0px !important;
}

#object-class {
  color: #fff;
}

p strong {
  color: #fff !important;
}

.code-toolbar {
filter: invert(90%) hue-rotate(175deg);
} 


pre[class*=language-]:before,pre[class*=language-]:after {
 box-shadow:0 0px 0px #979797;
}

pre.output {
    display: block;
    background-color: #090909;
    color: #c3c3c3;
    border-left: 10px solid #f00;

}

}
