css:
@-moz-document domain(web.roblox.com) {
/* Games page */

.games-page-left {
	margin: 0 16px;
}

.section-content, .stack-row {
	box-shadow: var(--layer-2);
	border-radius: 3px;
}

.tab-content {
	margin: 30px 0 0 0;
}

.tab-content {
	margin: 10px 0 0 0;
}

.gutter-ads .container-main {
	max-width: inherit;
}

/* Home */

body[data-internal-page-name="Home"] .content {
	width: 970px;
}

.home-page-left {
	margin: 0 16px;
}

.section-content, .stack-row {
	box-shadow: var(--layer-2);
	border-radius: 3px;
}

.tab-content {
	margin: 30px 0 0 0;
}

/*AD block*/

.ad{
display:none!important;    
}
.ad-annotations{
display:none!important;  
}

ad img {
    display: none
}
.abp .ad-identification-text {
    display: none
}
.abp .UpsellAdButton,
.abp .UpsellAdButton:link,
.abp .UpsellAdButton:visited,
.abp .BadAdButton,
.abp .BadAdButton:link,
.abp .BadAdButton:visited {
    display: none
}
.catalog-container {
    margin-top: 25px
}
}
