css:
@-moz-document url-prefix("https://ke.qq.com/webcourse/index.html")
{
/* 当前播放标题不省略 */
.current-task-name.header-item {
    max-width: none!important;
}
/* 播放列表长标题不省略 */
.playlist {
    width:auto!important;
}
/* 去除报名提醒 */
.study-header > .sign-tips.header-item
/* 去除分享按钮 */
,.study-header .operations .share.header-item
/* 去除腾讯视频LOGO */
,txpdiv.txp_bottom > txpdiv > txpdiv.txp_right_controls > a
{
    display:none!important;
}

}
